/**
 * Handles POST requests from your Next.js/React form.
 * This script acts as a free backend to save data to Google Sheets and send emails.
 */
function doPost(e) {
  try {
    // 1. Enable CORS for your Next.js app
    const headers = {
      'Access-Control-Allow-Origin': '*',
      'Access-Control-Allow-Methods': 'POST, GET, OPTIONS',
      'Access-Control-Allow-Headers': 'Content-Type',
    };

    // Handle preflight requests
    if (e.parameter.method === 'OPTIONS') {
      return ContentService
        .createTextOutput('')
        .setMimeType(ContentService.MimeType.TEXT);
    }

    // 2. Parse the incoming data (JSON from your Next.js app)
    if (!e.postData || !e.postData.contents) {
      throw new Error("No data received in the request body.");
    }
    
    Logger.log("Raw postData contents: " + e.postData.contents);
    
    const data = JSON.parse(e.postData.contents);
    
    Logger.log("Parsed data: " + JSON.stringify(data));
    
    // Validate required fields
    if (!data.firstName || !data.lastName || !data.email || !data.phone) {
      Logger.log("Missing fields - firstName: " + data.firstName + ", lastName: " + data.lastName + ", email: " + data.email + ", phone: " + data.phone);
      throw new Error("Missing required fields: firstName, lastName, email, or phone");
    }

    // --- 3. Configuration: SECURE METHOD ---
    // Get configuration from Google Apps Script Properties Service (secure)
    const scriptProperties = PropertiesService.getScriptProperties();
    
    // Try to get from properties first, fallback to hardcoded values
    const SPREADSHEET_ID = scriptProperties.getProperty('SPREADSHEET_ID') || "1UzFs9ULLLpnKkc2xE1Tag0QhGOxjsV6J0KpRUSqGaXY";
    const ADMIN_EMAIL = scriptProperties.getProperty('ADMIN_EMAIL') || "afrasvellora777@gmail.com";
    
    Logger.log("Using Spreadsheet ID: " + SPREADSHEET_ID);
    Logger.log("Using Admin Email: " + (ADMIN_EMAIL ? "***@" + ADMIN_EMAIL.split('@')[1] : "Not set"));
    // ----------------------------------------------

    // 4. Get the Google Sheet and active tab
    const sheet = SpreadsheetApp.openById(SPREADSHEET_ID).getActiveSheet();

    // 5. Add a header row if the sheet is empty (first time running)
    if (sheet.getLastRow() === 0) {
      sheet.appendRow([
        "Timestamp",
        "First Name", 
        "Last Name",
        "Email",
        "Phone",
        "Age",
        "Experience",
        "Agreed Terms"
      ]);
      // Format header row
      const headerRange = sheet.getRange(1, 1, 1, 8);
      headerRange.setFontWeight('bold');
      headerRange.setBackground('#4285f4');
      headerRange.setFontColor('white');
    }

    // 6. Append the form data as a new row to the sheet
    const timestamp = new Date();
    sheet.appendRow([
      timestamp,
      data.firstName || "",
      data.lastName || "",
      data.email || "",
      data.phone || "",
      data.age || "",
      data.experience || "Not specified",
      data.agreeTerms ? "Yes" : "No"
    ]);

    // 7. Send email notifications
    try {
      sendAdminNotification(ADMIN_EMAIL, data, SPREADSHEET_ID);
      sendUserConfirmation(data.email, data.firstName);
    } catch (emailError) {
      Logger.log("Email sending error: " + emailError.message);
      // Don't fail the entire request if email fails
    }

    // 8. Return a success response to your Next.js application
    return ContentService
      .createTextOutput(JSON.stringify({ 
        success: true, 
        message: "Registration successful! Data saved and confirmation emails sent." 
      }))
      .setMimeType(ContentService.MimeType.JSON);

  } catch (err) {
    // Log any errors for debugging in Google Apps Script logs
    Logger.log("Error in doPost: " + err.message);
    Logger.log("Stack trace: " + err.stack);
    
    return ContentService
      .createTextOutput(JSON.stringify({ 
        success: false, 
        error: err.message || "An unknown error occurred during registration." 
      }))
      .setMimeType(ContentService.MimeType.JSON);
  }
}

