// These declarations help the Apps Script linter, you don't need to change them.
var SpreadsheetApp;
var ContentService;
var Logger;
var MailApp;
var PropertiesService;

/**
 * SETUP FUNCTION: Run this once to securely store your sensitive configuration
 * This keeps your email and spreadsheet ID out of the visible code
 */
function setupSecureConfiguration() {
  const scriptProperties = PropertiesService.getScriptProperties();
  
  // Set your configuration here - run this function once, then delete these values
  scriptProperties.setProperties({
    'SPREADSHEET_ID': '1UzFs9ULLLpnKkc2xE1Tag0QhGOxjsV6J0KpRUSqGaXY',
    'ADMIN_EMAIL': 'afrasvellora777@gmail.com'
  });
  
  Logger.log("✅ Secure configuration saved!");
  Logger.log("You can now remove the hardcoded values from the setupSecureConfiguration function");
  
  // Test the configuration
  const testSpreadsheetId = scriptProperties.getProperty('SPREADSHEET_ID');
  const testAdminEmail = scriptProperties.getProperty('ADMIN_EMAIL');
  
  Logger.log("Spreadsheet ID saved: " + (testSpreadsheetId ? "✅" : "❌"));
  Logger.log("Admin Email saved: " + (testAdminEmail ? "✅" : "❌"));
}

/**
 * Helper function to clear configuration (if needed)
 */
function clearSecureConfiguration() {
  const scriptProperties = PropertiesService.getScriptProperties();
  scriptProperties.deleteProperty('SPREADSHEET_ID');
  scriptProperties.deleteProperty('ADMIN_EMAIL');
  Logger.log("🗑️ Secure configuration cleared!");
}
